import React from "react";
import arrowLeft from "./images/icon-prev.svg";
import arrowRight from "./images/icon-next.svg";
import Testimonialone from "./Testimonialone";
import {sliderPosition,setSliderPosition} from "./Testimonialone"

function Slider() {
  function handleClickPrev() {
      setSliderPosition("translate(-100%)")

  }

  function handleClickNext() {

  }

  return (
    <div className="slider_btn">
      <button
        onClick={handleClickPrev}
        className="slider__btn slider__previous"
      >
        <img src={arrowLeft} alt="previous-slide" />
      </button>
      <button onClick={handleClickNext} className="slider__btn slider__next">
        <img src={arrowRight} alt="next-slide" />
      </button>
    </div>
  );
}

export default Slider;
